p = getPrime(512)
q = getPrime(512)
assert p % 4 == 3
assert q % 4 == 3
n = p*q
e = 1024
c = pow(m, e, n)
print(f'n = {n}')
print(f'c = {c}')
print(f'leak = {p+q}')
